import { useState, useEffect } from 'react';
import { Container, Paper, Typography, TextField, Button, Box, Grid, Dialog, DialogTitle, DialogContent, DialogActions, Alert } from '@mui/material';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { ordersAPI } from '../services/api';
import { clearCart } from '../redux/cartSlice';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';

const Checkout = () => {
  const cartItems = useSelector(state => state.cart.items);
  const user = useSelector(state => state.auth.user);
  const isAuthenticated = useSelector(state => state.auth.isAuthenticated);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
    }
  }, [isAuthenticated, navigate]);

  const [loading, setLoading] = useState(false);
  const [orderComplete, setOrderComplete] = useState(false);
  const [orderId, setOrderId] = useState(null);
  const [error, setError] = useState('');

  const [shippingData, setShippingData] = useState({
    address: user?.address || '',
    phone: user?.phone || '',
    comment: '',
  });

  const totalAmount = cartItems.reduce((sum, item) => sum + item.price, 0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    if (!shippingData.address.trim() || !shippingData.phone.trim()) {
      setError('Пожалуйста, заполните адрес и телефон');
      setLoading(false);
      return;
    }

    if (!isAuthenticated) {
      setError('Необходимо войти в систему');
      setLoading(false);
      return;
    }

    try {
      const orderData = {
        items: cartItems.map(item => ({
          product: item.id,
          quantity: 1,
          price: item.price
        })),
        totalAmount,
        shippingAddress: shippingData.address,
      };

      const response = await ordersAPI.create(orderData);
      setOrderId(response.data._id);
      dispatch(clearCart());
      setOrderComplete(true);
    } catch (error) {
      setError('Произошла ошибка при оформлении заказа. Попробуйте позже.');
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    navigate('/orders');
  };

  if (cartItems.length === 0 && !orderComplete) {
    return (
      <Container maxWidth="sm" sx={{ py: 4 }}>
        <Paper sx={{ p: 3, textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Корзина пуста
          </Typography>
          <Button
            variant="contained"
            onClick={() => navigate('/products')}
            sx={{ mt: 2 }}
          >
            Перейти к покупкам
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom>
          Оформление заказа
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 3 }}>
            {error}
          </Alert>
        )}

        <Box component="form" onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Адрес доставки"
                required
                value={shippingData.address}
                onChange={(e) => setShippingData({ ...shippingData, address: e.target.value })}
                disabled={loading}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Телефон"
                required
                value={shippingData.phone}
                onChange={(e) => setShippingData({ ...shippingData, phone: e.target.value })}
                disabled={loading}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Комментарий к заказу"
                multiline
                rows={3}
                value={shippingData.comment}
                onChange={(e) => setShippingData({ ...shippingData, comment: e.target.value })}
                disabled={loading}
              />
            </Grid>
          </Grid>

          <Box sx={{ mt: 4 }}>
            <Typography variant="h6" gutterBottom>
              Ваш заказ
            </Typography>
            {cartItems.map((item, index) => (
              <Box key={item.id || index} sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography>{item.name}</Typography>
                <Typography>{item.price.toLocaleString()} ₽</Typography>
              </Box>
            ))}
            <Box sx={{ mt: 2, pt: 2, borderTop: 1, borderColor: 'divider' }}>
              <Typography variant="h6">
                Итого: {totalAmount.toLocaleString()} ₽
              </Typography>
            </Box>
          </Box>

          <Button
            type="submit"
            variant="contained"
            fullWidth
            size="large"
            sx={{ mt: 3 }}
            disabled={loading}
          >
            {loading ? 'Оформление...' : 'Оформить заказ'}
          </Button>
        </Box>
      </Paper>

      <Dialog open={orderComplete} onClose={handleClose}>
        <DialogTitle>Заказ успешно оформлен!</DialogTitle>
        <DialogContent>
          <Box sx={{ textAlign: 'center', py: 2 }}>
            <CheckCircleOutlineIcon color="success" sx={{ fontSize: 60, mb: 2 }} />
            <Typography variant="h6" gutterBottom>
              Номер заказа: {orderId}
            </Typography>
            <Typography color="text.secondary">
              Мы отправили подтверждение на вашу почту
            </Typography>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} variant="contained">
            Перейти к моим заказам
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Checkout;
